mapper.select:
SELECT qna_idx, mbsp_id, admin_id, question, answer, anscheck, question_date, answer_date, pro_name, pro_up_folder, pro_img FROM( SELECT /*+ INDEX_DESC(qnaboard_tbl pk_qna_idx) */ ROWNUM AS rn, q.qna_idx, q.mbsp_id, q.admin_id, q.question, q.answer, q.anscheck, q.question_date, q.answer_date, p.pro_name, p.pro_up_folder, p.pro_img FROM qnaboard_tbl q INNER JOIN product_tbl p ON q.pro_num = p.pro_num WHERE
ROWNUM <= (#{pageNum} * #{amount})
) WHERE rn > (#{pageNum} - 1 ) * #{amount}

mapper.update:
UPDATE
	qnaboard_tbl
SET
	answer = #{answer}
WHERE
	qna_idx = #{qna_idx}
